package com.innocv.otpdispatcherimport android.support.v7.widget.RecyclerViewimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport kotlinx.android.extensions.LayoutContainerimport kotlinx.android.synthetic.main.sms_card_layout.*class OtpMessageAdapter : RecyclerView.Adapter<OtpMessageAdapter.SmsMessageViewHolder>() {    class SmsMessageViewHolder(override val containerView: View) : RecyclerView.ViewHolder(containerView), LayoutContainer {        fun bindMessage(otpMessage: OtpMessage) {            senderLabel.text = otpMessage.sender            messageLabel.text = otpMessage.message        }    }    private var dataSource: MutableList<OtpMessage> = mutableListOf()    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): SmsMessageViewHolder {        val smsCardLayout = LayoutInflater.from(parent.context).inflate(R.layout.sms_card_layout, parent, false)        return SmsMessageViewHolder(smsCardLayout)    }    override fun getItemCount(): Int {        return dataSource.size    }    override fun onBindViewHolder(holder: SmsMessageViewHolder, position: Int) {        val smsMessage = dataSource[position]        holder.bindMessage(smsMessage)    }    fun addSmsMessage(otpMessage: OtpMessage) {        val position = dataSource.size        dataSource.add(position, otpMessage)        notifyItemInserted(position)    }}